import React from "react";
import axios from "axios";
import StripeCheckout from "react-stripe-checkout";

const StripeCheckoutButton = ({ price }) => {
	const priceForStripe = price * 100;
	const publishableKey =
		"pk_test_SXMHLJ4Co9oi9DXIRkPABU8b00bXnJPVHG";
	const onToken = token => {
		axios({
			url: "payment",
			method: "post",
			data: {
				amount: priceForStripe,
				token
			}
		})
			.then(response => {
				alert("Payment Successful");
			})
			.catch(error => {
				console.log("Payment error: " + JSON.parse(error));
				alert(
					"There was an issue with your payment. Please make sure your credentials were entered correctly."
				);
			});
	};
	return (
		<StripeCheckout
			label="Pay Now"
			name="CRWN Clothing Ltd."
			billingAddress
			shippingAddress
			image="http://www.innalabs.com/wp-content/uploads/2017/08/004-credit-card.svg"
			description={`Your total is ${price}`}
			amount={priceForStripe}
			panelLabel="Pay Now"
			token={onToken}
			stripeKey={publishableKey}
		/>
	);
};

export default StripeCheckoutButton;
